using SpendSmart.Services; using System; namespace SpendSmart.Views { public class Dashboard { private readonly FinanceService _financeService; public Dashboard() { _financeService = new FinanceService(); } public void Display() { Console.WriteLine("1. Add Transaction"); Console.WriteLine("2. View Total Spent"); Console.Write("Choose an option: "); var option = Console.ReadLine(); switch (option) { case "1": AddTransaction(); break; case "2": ViewTotalSpent(); break; default: Console.WriteLine("Invalid option."); break; } } private void AddTransaction() { Console.Write("Enter amount: "); var amount = decimal.Parse(Console.ReadLine()); Console.Write("Enter description: "); var description = Console.ReadLine(); var transaction = new Models.Transaction { Amount = amount, Date = DateTime.Now, Description = description, Category = new Models.Category { Name = "General" } }; _financeService.AddTransaction(transaction); Display(); } private void ViewTotalSpent() { Console.WriteLine($"Total Spent: {_financeService.GetTotalSpent()}"); Display(); } } } 
